mapper.select:
SELECT id, name, email
FROM user
WHERE social_service_type = #{serviceType}
  AND social_service_unique_id = #{serviceUniqueId}

SELECT count(*)
FROM user_keyword
WHERE user_id = #{userId}

SELECT keyword
FROM user_keyword
WHERE user_id = #{userId}

SELECT count(*)
FROM user_keyword
WHERE user_id = #{userId}

mapper.insert:
INSERT IGNORE INTO user_keyword(user_id, keyword)
VALUES

  #{keyword.userId}, #{keyword.keyword}

mapper.delete:
DELETE FROM user_keyword
WHERE user_id = #{userId}
  AND keyword IN

    #{keyword}

mapper.select:
SELECT count(*)
FROM user_bookmark
WHERE user_id = #{userId}

SELECT id, news_url
FROM user_bookmark
WHERE user_id = #{userId}
LIMIT #{offset}, #{limit}
